import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http'
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  data!: any[];
  constructor(private http: HttpClient) { }
  items: any[] = []
  ngOnInit() {
    this.http.get<any[]>('assets/recipes.json').subscribe((response) => {
      this.data = response;
      for (let key in this.data) {
        this.items.push(key)
        console.log(this.data[0])
      }
    });
  }

  title = 'logi.tools';
  // items = ['Item 1', 'Item 2', 'Item 3', 'Item 4'];
  processings = ['Processing 1', 'Processing 2', 'Processing 3', 'Processing 4'];

  selectedItem!: string;
  selectedProcess!: string;
  selectedItems: { item: string, processing: string }[] = [];
  onItemSelect() {
    if (this.selectedItem) {
      this.selectedProcess = '';
    }
    else {
      this.selectedProcess = '';
    }

  }
  onSubmit() {
    const newItem = { item: this.selectedItem, processing: this.selectedProcess };
    this.selectedItems.push(newItem);
    this.selectedItem = '';
    this.selectedProcess = '';
  }

  removeItem(index: number) {
    this.selectedItems.splice(index, 1);
  }

}
