import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'logi.tools';

  items: string[] = []
  itemOptions = ['test1', 'test2']

  processings: string[] = []
  processingOptions = ['testp1', 'testp2']

  removeItem(index: number) {
    this.items.splice(index, 1);
    this.processings.splice(index, 1);
  }
  addItem() {
    this.items.push('');
    this.processings.push('');
  }
  submitForm() {
    console.log(`Selected items: ${this.items}`);
    console.log(`Selected processings: ${this.processings}`);
    // Send selected items and processings to server for processing
  }

}
